mapper.select:
	select 

  from app_resource


       and RESOURCE_ID = #{resourceId,jdbcType=VARCHAR}


      and RESOURCE_NAME like concat('%', #{resourceName,jdbcType=VARCHAR} , '%')


      and RESOURCE_TYPE = #{resourceType,jdbcType=VARCHAR}


  order by CREATED_DATE desc

select RESOURCE_ID, RESOURCE_NAME, RESOURCE_TYPE, RESOURCE_LEVEL, PARENT_RESOURCE_ID, RESOURCE_ICON_CLASS, ACTION_URL, END_FLAG, DISPLAY_ORDER from app_resource r where r.RESOURCE_ID in ( select RESOURCE_ID from app_role_resource rr where rr.ROLE_ID in(
#{roleId}
) )
